class juego3 {
private:
    persona1* objp2;
    macetero* objmacetero;
    int segundos = 250;
    int contagua = 9;
    int contsemillas = 9;
    int planta = 0;
    bool juegoActivo = true;
public:
    juego3() {
        objp2 = new persona1(55, 15, 6);
        objmacetero = new macetero(rand() % 110 + 5, rand() % 27 + 5);
    }
    ~juego3() {
        delete objp2;
        delete objmacetero;
    }
    bool perdiste() {
        if (segundos == 0 && planta <70) {
            Console::Clear();
            lose();
            return true;
        }
        return false;
    }
    void temporizador() {
        gotoxy(1, 1, "TIEMPO: "); cout << segundos;
        gotoxy(11, 1, " ");
        segundos--;
    }
    void plantado() {
        gotoxy(60, 1, "Mundo Salvado: "); planta += 10; cout << planta;
        gotoxy(80, 1, "% ");
        _getch();
    }
    
    void semillas() {
        gotoxy(20, 1, "SEMILLAS:    ");
        gotoxy(20, 1, "SEMILLAS: "); cout << contsemillas--;
        _getch();
    }
    void agua() {
        gotoxy(40, 1, "AGUA:   ");
        gotoxy(40, 1, "AGUA: "); cout << contagua--;
        _getch();
    }
    bool colision() {
            if (((objmacetero->getX() + 5 >= objp2->getX()-1) &&
                (objmacetero->getX() + 5 < objp2->getX() + 3) &&
                (objmacetero->getY() == objp2->getY())) || 
                ((objmacetero->getX() + 5 >= objp2->getX()-1) &&
                (objmacetero->getX() + 5 < objp2->getX() + 3) &&
                (objmacetero->getY() == objp2->getY()+1))) {
                return true;
            }
        return false;
    }
    void WIN() {
        if ((planta == 100) || (planta >= 70 && segundos == 0)) {
            juegoActivo = false; // Cambia el estado a inactivo
            if (_getch()) {
                Console::Clear();
            }
            win();
            if (_getch()) {
                Console::Clear();
            }
            owners();
            system("pause>0");
        }
    }
    void movimiento3() {
        mapa3();
        while (juegoActivo)
        {
            temporizador();
            objp2->borrar();
            objp2->mover3();
            objp2->dibujar2();
            objmacetero->dibujar_macetero();
            if (colision()) {
                objmacetero->borrar_macetero();
                objmacetero->setX(rand() % 110 + 5);
                objmacetero->setY(rand() % 27 + 5);
                objmacetero->dibujar_macetero();
                semillas();
                plantado();
                agua();
            }
            perdiste();
            WIN();
            sleep_for(milliseconds(100));
        }

    }
};
